sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
	if(g_form.getValue('impact')==1 && g_form.getValue('urgency')==1 && !g_user.hasRoleExactly('x_58872_needit.major_inc_mgr')){
		var ans = confirm("select ok to submit or cancel");
		if(!ans){
			g_form.addInfoMessage("Incident not submitted");
			g_form.addInfoMessage("If base information is not available then use additional data field");
			g_form.showFieldMsg('category','Major incident base field');
			g_form.showFieldMsg('cmdb_ci','Major incident base field');
			g_form.showFieldMsg('assignment_group','Major incident base field');
			g_form.showFieldMsg('short_description','Major incident base field');
		}
		return ans;
	}

}
